#include <curl/curl.h>
#include <curl/easy.h>

namespace swish {

namespace http {

enum version : int64_t {
  Version_1_0 = CURL_HTTP_VERSION_1_0,
  Version_1_1 = CURL_HTTP_VERSION_1_1,
  Version_2 = CURL_HTTP_VERSION_2,
  Version_2_0 = CURL_HTTP_VERSION_2_0,
  Version_2_PriorKnowledge = CURL_HTTP_VERSION_2_PRIOR_KNOWLEDGE,

  // default
  Version_2_TLS = CURL_HTTP_VERSION_2TLS,

  Version_2_Last = CURL_HTTP_VERSION_LAST,
  Version_None = CURL_HTTP_VERSION_NONE,

};
};

namespace http {
enum class ResponseCode {
  Continue = 100,
  SwitchingProtocols = 101,
  OK = 200,
  Created = 201,
  Accepted = 202,
  NonAuthoritativeInformation = 203,
  NoContent = 204,
  ResetContent = 205,
  PartialContent = 206,
  MultipleChoices = 300,
  MovedPermanently = 301,
  Found = 302,
  SeeOther = 303,
  NotModified = 304,
  TemporaryRedirect = 307,
  PermanentRedirect = 308,
  BadRequest = 400,
  Unauthorized = 401,
  Forbidden = 403,
  NotFound = 404,
  MethodNotAllowed = 405,
  NotAcceptable = 406,
  ProxyAuthenticationRequired = 407,
  RequestTimeout = 408,
  Conflict = 409,
  Gone = 410,
  LengthRequired = 411,
  PreconditionFailed = 412,
  PayloadTooLarge = 413,
  URITooLong = 414,
  UnsupportedMediaType = 415,
  RangeNotSatisfiable = 416,
  ExpectationFailed = 417,
  ImATeapot = 418,
  UnprocessableEntity = 422,
  TooEarly = 425,
  UpgradeRequired = 426,
  PreconditionRequired = 428,
  TooManyRequests = 429,
  RequestHeaderFieldsTooLarge = 431,
  UnavailableForLegalReasons = 451,
  InternalServerError = 500,
  NotImplemented = 501,
  BadGateway = 502,
  ServiceUnavailable = 503,
  GatewayTimeout = 504,
  HTTPVersionNotSupported = 505,
  NetworkAuthenticationRequired = 511
};
};
};  // namespace swish
#endif